-- Re-create matches table with perfect structure for scraped data
DROP TABLE IF EXISTS matches;

CREATE TABLE matches (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    category TEXT,
    league TEXT,
    source TEXT,
    date DATE,
    time TEXT,
    home_team TEXT,
    away_team TEXT,
    home_team_logo TEXT,
    away_team_logo TEXT,
    location TEXT,
    score TEXT,
    winner_in_draw TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL
);

